import { Component, OnDestroy } from '@angular/core';
import { AuthService } from '../Authentication/auth.service';
import { Subscription } from 'rxjs';
import {User} from "oidc-client";

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent implements OnDestroy {
  isExpanded = false;
  isAuthenticated = false;
  username: string = '';
  private authSubscription: Subscription;

  constructor(private authService: AuthService) {
    this.authSubscription = this.authService.currentUser.subscribe(user => {
      this.isAuthenticated = !!user;
      this.username = user ? user.username : ''; // Assuming 'username' is the property
    });
  }

  ngOnDestroy() {
    this.authSubscription.unsubscribe();
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }

  logout() {
    if (confirm('Are you sure you want to log out?')) {
      this.authService.logout();
      // Redirect to home page or perform additional actions
    }
  }

  protected readonly User = User;
}
<header>
  <nav class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3">
    <div class="container">
      <div class="nav-sections">
        <!-- Left Section -->
        <div class="nav-left">
          <a class="navbar-brand" [routerLink]="['/']">HomeToGo</a>
        </div>

        <!-- Middle Section -->
        <div class="navbar-collapse collapse" [ngClass]="{ show: isExpanded }">
          <ul class="navbar-nav">
            <li class="nav-item" [routerLinkActive]="['link-active']">
              <a class="nav-link text-dark" [routerLink]="['/listingsgrid']">Listings</a>
            </li>
            <li class="nav-item" [routerLinkActive]="['link-active']">
              <a class="nav-link text-dark" [routerLink]="['/reservations']">Reservations</a>
            </li>
          </ul>
        </div>

        <!-- Right Section -->
        <div class="nav-right">
          <ul class="navbar-nav">
            <li class="nav-item" *ngIf="!isAuthenticated">
              <a class="nav-link text-dark" [routerLink]="['/login']">Login</a>
            </li>
            <li class="nav-item" *ngIf="!isAuthenticated">
              <a class="nav-link text-dark" [routerLink]="['/register']">Register</a>
            </li>

            <li *ngIf="isAuthenticated" class="nav-item">
              <span class="nav-link">Welcome, {{ User.name }}</span>
            </li>
            <li class="nav-item" *ngIf="isAuthenticated">
              <button type="button" class="btn btn-secondary btn-sm"> <a class="nav-link text-dark" (click)="logout()">Logout</a></button>
            </li>
          </ul>
        </div>
      </div>
    </div>
  </nav>
</header>
