import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IListing } from './listing';
import { ListingService } from './listings.service';

@Component({
  selector: 'app-listing-detail',
  templateUrl: './listingdetail.component.html',
  styleUrls: ['./listingdetail.component.css']
})
export class ListingDetailComponent implements OnInit {
  listing!: IListing; // The non-null assertion operator '!' tells TypeScript that 'listing' will be assigned.


  constructor(
    private route: ActivatedRoute,
    private listingService: ListingService
  ) { }

  ngOnInit(): void {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam !== null) {
      const id = +idParam;
      this.listingService.getListingById(id).subscribe(
        (data: IListing) => {
          this.listing = data;
        },
        error => {
          console.error('Error fetching listing details', error);
        }
      );
    } else {
      // Handle the case where ID is null, perhaps redirect back or show an error message
      console.error('No listing ID provided in the route.');
    }
  }



}
<div class="container mt-4">
    <div class="row gx-5">
        <div class="col-md-6">
            <img [alt]="listing.Title" [src]="listing.ImageUrl" class="img-fluid rounded shadow-sm" />
        </div>
        <div class="col-md-6">
            <h1>{{ listing.Title }}</h1>
            <p class="text-muted fs-4">{{ listing.Price | currency:'NOK' }}</p>
            <p>{{ listing.Description }}</p>
            <div class="mt-4">
                <button class="btn btn-primary"  [routerLink]="['./reservations/reservationform']">Create a Reservation</button>
            </div>
            <div class="mt-3">
                <a [routerLink]="['/listings']" class="btn btn-outline-secondary">Back to Table View</a>
                <a [routerLink]="['/listingsgrid']" class="btn btn-outline-secondary ms-2">Back to Grid View</a>
            </div>
        </div>
    </div>
</div>
