import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  // Example properties (replace or remove based on actual usage)
  title: string = 'Welcome to HomeToGo';
  featuredRentals: string = 'Featured Rentals';

  // Example for future dynamic content loading
  carouselImages: string[] = [];
  testimonials: string[] = [];

  constructor() { }

  ngOnInit(): void {
    // Load dynamic content here (e.g., fetch data from a service)
    this.loadCarouselImages();
    this.loadTestimonials();
  }

  // Example methods for future data loading
  loadCarouselImages(): void {
    // Placeholder for actual data fetching logic
    this.carouselImages = [
      'assets/images/Ap1.jpg',
      'assets/images/Ap5.jpg',
      'assets/images/Ap2.jpg'
    ];
  }

  loadTestimonials(): void {
    // Placeholder for actual data fetching logic
    this.testimonials = [
      '"Booking with HomeToGo was a breeze. The host was friendly, the home was cozy..." - Sarah M.'
      // Add more testimonials here
    ];
  }
}
<div class="text-center">
  <h1 class="display-4">Welcome to HomeToGo</h1>
  <h2 class="display-6">Featured Rentals</h2>
</div>

<!-- Carousel Section -->
<div id="carouselImages" class="carousel slide" data-bs-ride="carousel">
  <div class="carousel-inner">
    <div class="carousel-item active">
      <img src="assets/Images/Ap5.jpg" class="d-block w-100" alt="Image 1">
    </div>
    <div class="carousel-item">
      <img src="assets/Images/Ap1.jpg" class="d-block w-100" alt="Image 2">
    </div>
    <div class="carousel-item">
      <img src="assets/Images/Ap2.jpg" class="d-block w-100" alt="Image 3">
    </div>
  </div>
  <button class="carousel-control-prev" type="button" data-bs-target="#carouselImages" data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button class="carousel-control-next" type="button" data-bs-target="#carouselImages" data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>

  <!-- Static 'Book Now' Button -->
  <a href="/reservations" class="btn book-now-btn">Book Now</a>
</div>

<!-- Information Cards -->
<!-- (Remainder of the HTML content) -->


<!-- Information Cards -->
<div class="card-container">
    <div class="card">
        <div class="card-content">
            <h3>About Us</h3>
            <p>HomeToGo is your gateway to discovering cozy homes, exciting experiences, and new adventures...</p>
        </div>
    </div>
    <div class="card">
        <div class="card-content">
            <h3>How It Works</h3>
            <p>Begin your journey with HomeToGo. Browse through a myriad of listings, connect with hosts...</p>
        </div>
    </div>
    <div class="card">
        <div class="card-content">
            <h3>Why Choose HomeToGo</h3>
            <p>With HomeToGo, you're not just booking a place to stay, but a home away from home...</p>
        </div>
    </div>
    <div class="card">
        <div class="card-content">
            <h3>Testimonials</h3>
            <p>"Booking with HomeToGo was a breeze. The host was friendly, the home was cozy..." - Sarah M.</p>
        </div>
    </div>
</div>
