import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { IReservation } from './reservation';

@Injectable({
  providedIn: 'root'
})
export class ReservationService {
  private baseUrl = 'api/reservation';

  constructor(private http: HttpClient) { }

  getReservations(): Observable<IReservation[]> {
    return this.http.get<IReservation[]>(this.baseUrl);
  }

  createReservation(newReservation: IReservation): Observable<any> {
    newReservation.ListingId = parseInt(newReservation.ListingId, 10); // Convert ListingId to an integer
    const createUrl = 'api/reservation/create';
    console.log('Sending reservation:', newReservation);

    return this.http.post<any>(createUrl, newReservation);
  }


  deleteReservation(id: number): Observable<any> {
    return this.http.delete<any>(`${this.baseUrl}/${id}`);
  }
}
