import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router'

@Component({
  selector: "app-listings-listingform",
  templateUrl: "./listingform.component.html"
})
export class ListingFormComponent {
  listingForm: FormGroup;

  constructor(private _formbuilder: FormBuilder, private _router: Router) {
    this.listingForm = _formbuilder.group({
      title: ['', Validators.required],
      address: ['', Validators.required],
      price: ['', Validators.required],
      description: [''],
      imageUrl: ['']
    })
  }


  onSubmit() {
    console.log("ListingCreate form submitted: ");
    console.log(this.listingForm);
    console.log("the listing" + this.listingForm.value.title + " is created.");
    console.log(this.listingForm.touched);
  }

  backToListings() {
    this._listing.navigate(['/listings']);
  }
}
